mapper.update:
UPDATE parents
account = #{account}, password = #{password}, name = #{name}, phone = #{phone}, sex = #{sex}, is_deleted = #{isDeleted},
WHERE id = #{id}

mapper.select:
SELECT
FROM parents p LEFT OUTER JOIN stuparent sp ON sp.parent_id = p.id LEFT OUTER JOIN students s ON s.id = sp.student_id
p.account = #{parent.account, jdbcType=VARCHAR} AND p.name = #{parent.name, jdbcType=VARCHAR}
LIMIT #{page.startIndex} , #{page.pageSize}
