mapper.update:
UPDATE students
account = #{account}, name = #{name}, sex = #{sex}, id_card = #{idCard}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT *
FROM students
WHERE account = #{student.getAccount()}
  AND name = #{student.getName()} LIMIT #{page.getStartIndex()}
    , #{page.getPageSize()};
